sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	try {
		var inputs = {};
		inputs['ah_task'] = ''; // GlideRecord of table: task 

		// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
		// sn_fd.FlowAPI.getRunner().action('sn_itsm_spoke.create_incident').inBackground().withInputs(inputs).run();

		// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
		var result = sn_fd.FlowAPI.getRunner().action('sn_itsm_spoke.create_incident').inForeground().run();
		var outputs = result.getOutputs();

		// Get Outputs:
		// Note: outputs can only be retrieved when executing synchronously.
		var Incident = outputs['Incident']; // Document ID

		//Create a body object.  Add property value pairs to the body.
		var body = {};
		body.numInc = Incident.number;
		body.result = {
			"result": "Success"
		};
		response.setBody(body);

	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
		response.setBody(message);
	}
})(request, response);
